(gen_random_uuid(), 'Zalm met dille', 'Gebakken zalm met verse dille en citroen, licht en verfijnd.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 15, 25, 'Makkelijk', 4, '[{"name": "zalmfilet", "quantity": "600", "unit": "g"}, {"name": "dille", "quantity": "1", "unit": "bos"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "boter", "quantity": "50", "unit": "g"}, {"name": "knoflook", "quantity": "2", "unit": "teentjes"}, {"name": "peper", "quantity": "1", "unit": "tl"}]'::jsonb, '[{"step": 1, "instruction": "Bestrooi de zalm met zout en peper."}, {"step": 2, "instruction": "Bak de zalm 4-5 minuten per kant in boter."}, {"step": 3, "instruction": "Voeg knoflook en dille toe aan de pan."}, {"step": 4, "instruction": "Pers citroen over de zalm."}, {"step": 5, "instruction": "Serveer met gebakken aardappelen en groenten."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Tonijnsteak met sesam', 'Gebakken tonijnsteak met sesamzaadjes en sojasaus, een Aziatische klassieker.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 8, 18, 'Makkelijk', 2, '[{"name": "tonijnsteak", "quantity": "2", "unit": "stuk"}, {"name": "sesamzaadjes", "quantity": "3", "unit": "el"}, {"name": "sojasaus", "quantity": "3", "unit": "el"}, {"name": "gember", "quantity": "2", "unit": "cm"}, {"name": "knoflook", "quantity": "2", "unit": "teentjes"}, {"name": "olijfolie", "quantity": "2", "unit": "el"}]'::jsonb, '[{"step": 1, "instruction": "Marineer de tonijn in sojasaus, gember en knoflook."}, {"step": 2, "instruction": "Bestrooi met sesamzaadjes."}, {"step": 3, "instruction": "Bak de tonijn 2-3 minuten per kant in een hete pan."}, {"step": 4, "instruction": "De binnenkant moet roze blijven."}, {"step": 5, "instruction": "Serveer met rijst en groenten."}]'::jsonb, ARRAY['Vis', 'Aziatisch', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Kabeljauw met chorizo', 'Gebakken kabeljauw met chorizo en tomaten, een Spaanse combinatie.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 20, 35, 'Gemiddeld', 4, '[{"name": "kabeljauwfilet", "quantity": "600", "unit": "g"}, {"name": "chorizo", "quantity": "150", "unit": "g"}, {"name": "tomaten", "quantity": "400", "unit": "g"}, {"name": "ui", "quantity": "1", "unit": "stuk"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}, {"name": "witte wijn", "quantity": "100", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Bak de chorizo uit in een pan."}, {"step": 2, "instruction": "Voeg ui en knoflook toe."}, {"step": 3, "instruction": "Voeg tomaten en wijn toe en laat pruttelen."}, {"step": 4, "instruction": "Leg de kabeljauw erop en laat 10 minuten garen."}, {"step": 5, "instruction": "Serveer met brood of aardappelen."}]'::jsonb, ARRAY['Vis', 'Spaans', 'Comfort Food'], 'Vis'),
(gen_random_uuid(), 'Garnalen met knoflook', 'Grote garnalen gebakken in knoflook en olijfolie, een mediterrane klassieker.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 10, 20, 'Makkelijk', 4, '[{"name": "grote garnalen", "quantity": "600", "unit": "g"}, {"name": "knoflook", "quantity": "6", "unit": "teentjes"}, {"name": "olijfolie", "quantity": "4", "unit": "el"}, {"name": "peterselie", "quantity": "1", "unit": "bos"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "peper", "quantity": "1", "unit": "tl"}]'::jsonb, '[{"step": 1, "instruction": "Pel de garnalen maar laat de staart zitten."}, {"step": 2, "instruction": "Bak de knoflook zachtjes in olijfolie."}, {"step": 3, "instruction": "Voeg de garnalen toe en bak 3-4 minuten."}, {"step": 4, "instruction": "Voeg peterselie en citroensap toe."}, {"step": 5, "instruction": "Serveer direct met brood om de saus op te dippen."}]'::jsonb, ARRAY['Vis', 'Mediterraan', 'Feest'], 'Vis'),
(gen_random_uuid(), 'Forel met amandelen', 'Gebakken forel met gebrande amandelen en boter, een klassieke combinatie.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 15, 25, 'Makkelijk', 4, '[{"name": "forel", "quantity": "4", "unit": "stuk"}, {"name": "amandelen", "quantity": "100", "unit": "g"}, {"name": "boter", "quantity": "80", "unit": "g"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "peterselie", "quantity": "1", "unit": "bos"}, {"name": "peper", "quantity": "1", "unit": "tl"}]'::jsonb, '[{"step": 1, "instruction": "Bestrooi de forel met zout en peper."}, {"step": 2, "instruction": "Bak de forel 4-5 minuten per kant in boter."}, {"step": 3, "instruction": "Bak de amandelen goudbruin in boter."}, {"step": 4, "instruction": "Voeg peterselie en citroensap toe."}, {"step": 5, "instruction": "Serveer de forel met de amandelen erover."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Zwaardvis met citroen', 'Gegrilde zwaardvis met citroen en kruiden, stevig en smaakvol.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 12, 22, 'Makkelijk', 4, '[{"name": "zwaardvis", "quantity": "600", "unit": "g"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "olijfolie", "quantity": "3", "unit": "el"}, {"name": "tijm", "quantity": "2", "unit": "takjes"}, {"name": "knoflook", "quantity": "2", "unit": "teentjes"}, {"name": "peper", "quantity": "1", "unit": "tl"}]'::jsonb, '[{"step": 1, "instruction": "Marineer de zwaardvis in olijfolie, citroen, tijm en knoflook."}, {"step": 2, "instruction": "Grill de zwaardvis 5-6 minuten per kant."}, {"step": 3, "instruction": "Bestrooi met zout en peper."}, {"step": 4, "instruction": "Pers citroen over de vis."}, {"step": 5, "instruction": "Serveer met gebakken groenten."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Kabeljauw met erwtenpuree', 'Gebakken kabeljauw op een bedje van erwtenpuree, modern en verfijnd.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 25, 40, 'Gemiddeld', 4, '[{"name": "kabeljauwfilet", "quantity": "600", "unit": "g"}, {"name": "doperwten", "quantity": "400", "unit": "g"}, {"name": "room", "quantity": "100", "unit": "ml"}, {"name": "munt", "quantity": "1", "unit": "bos"}, {"name": "boter", "quantity": "30", "unit": "g"}, {"name": "citroen", "quantity": "1", "unit": "stuk"}]'::jsonb, '[{"step": 1, "instruction": "Kook de erwten 5 minuten."}, {"step": 2, "instruction": "Pureer de erwten met room, munt en boter."}, {"step": 3, "instruction": "Bak de kabeljauw 4-5 minuten per kant."}, {"step": 4, "instruction": "Serveer de kabeljauw op de erwtenpuree."}, {"step": 5, "instruction": "Afwerken met citroen en munt."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Sint-Jacobsschelpen', 'Gebakken sint-jacobsschelpen met boter en knoflook, een delicatesse.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 8, 18, 'Makkelijk', 4, '[{"name": "sint-jacobsschelpen", "quantity": "12", "unit": "stuk"}, {"name": "boter", "quantity": "60", "unit": "g"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}, {"name": "peterselie", "quantity": "1", "unit": "bos"}, {"name": "citroen", "quantity": "1", "unit": "stuk"}, {"name": "witte wijn", "quantity": "50", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Droog de schelpen goed af."}, {"step": 2, "instruction": "Bak ze 2 minuten per kant in hete boter."}, {"step": 3, "instruction": "Voeg knoflook en wijn toe."}, {"step": 4, "instruction": "Voeg peterselie en citroensap toe."}, {"step": 5, "instruction": "Serveer direct, de schelpen moeten nog zacht zijn."}]'::jsonb, ARRAY['Vis', 'Feest'], 'Vis'),
(gen_random_uuid(), 'Zalm Teriyaki', 'Zalm in een zoete teriyaki saus, een Japanse klassieker.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 15, 25, 'Makkelijk', 4, '[{"name": "zalmfilet", "quantity": "600", "unit": "g"}, {"name": "sojasaus", "quantity": "4", "unit": "el"}, {"name": "mirin", "quantity": "2", "unit": "el"}, {"name": "suiker", "quantity": "2", "unit": "el"}, {"name": "gember", "quantity": "2", "unit": "cm"}, {"name": "knoflook", "quantity": "2", "unit": "teentjes"}]'::jsonb, '[{"step": 1, "instruction": "Maak teriyaki saus met sojasaus, mirin en suiker."}, {"step": 2, "instruction": "Marineer de zalm 30 minuten in de saus."}, {"step": 3, "instruction": "Bak de zalm 4-5 minuten per kant."}, {"step": 4, "instruction": "Giet de teriyaki saus erover en laat glazeren."}, {"step": 5, "instruction": "Serveer met rijst en groenten."}]'::jsonb, ARRAY['Vis', 'Aziatisch', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Kabeljauw met tomaat en olijven', 'Mediterrane kabeljauw met tomaten en olijven, vol van smaak.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 25, 40, 'Gemiddeld', 4, '[{"name": "kabeljauwfilet", "quantity": "600", "unit": "g"}, {"name": "tomaten", "quantity": "400", "unit": "g"}, {"name": "zwarte olijven", "quantity": "100", "unit": "g"}, {"name": "kappertjes", "quantity": "2", "unit": "el"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}, {"name": "witte wijn", "quantity": "100", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Bak de knoflook zachtjes in olijfolie."}, {"step": 2, "instruction": "Voeg tomaten, olijven en kappertjes toe."}, {"step": 3, "instruction": "Giet wijn erbij en laat pruttelen."}, {"step": 4, "instruction": "Leg de kabeljauw erop en laat 10 minuten garen."}, {"step": 5, "instruction": "Serveer met pasta of brood."}]'::jsonb, ARRAY['Vis', 'Mediterraan', 'Comfort Food'], 'Vis'),
(gen_random_uuid(), 'Garnalen Scampi', 'Grote scampi gebakken in knoflookboter, een Italiaanse favoriet.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 10, 20, 'Makkelijk', 4, '[{"name": "scampi", "quantity": "600", "unit": "g"}, {"name": "boter", "quantity": "80", "unit": "g"}, {"name": "knoflook", "quantity": "4", "unit": "teentjes"}, {"name": "peterselie", "quantity": "1", "unit": "bos"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "witte wijn", "quantity": "50", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Pel de scampi maar laat de staart zitten."}, {"step": 2, "instruction": "Bak de knoflook zachtjes in boter."}, {"step": 3, "instruction": "Voeg de scampi toe en bak 3-4 minuten."}, {"step": 4, "instruction": "Voeg wijn, peterselie en citroensap toe."}, {"step": 5, "instruction": "Serveer direct met brood."}]'::jsonb, ARRAY['Vis', 'Italiaans', 'Feest'], 'Vis'),
(gen_random_uuid(), 'Zalm met pesto', 'Gebakken zalm met zelfgemaakte pesto, een Italiaanse twist.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 15, 30, 'Makkelijk', 4, '[{"name": "zalmfilet", "quantity": "600", "unit": "g"}, {"name": "basilicum", "quantity": "1", "unit": "bos"}, {"name": "pijnboompitten", "quantity": "50", "unit": "g"}, {"name": "parmezaan", "quantity": "50", "unit": "g"}, {"name": "olijfolie", "quantity": "100", "unit": "ml"}, {"name": "knoflook", "quantity": "2", "unit": "teentjes"}]'::jsonb, '[{"step": 1, "instruction": "Maak pesto met basilicum, pijnboompitten, parmezaan, knoflook en olijfolie."}, {"step": 2, "instruction": "Bestrijk de zalm met pesto."}, {"step": 3, "instruction": "Bak de zalm 4-5 minuten per kant."}, {"step": 4, "instruction": "Serveer met extra pesto erover."}, {"step": 5, "instruction": "Lekker met pasta of gebakken groenten."}]'::jsonb, ARRAY['Vis', 'Italiaans', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Kabeljauw met chorizo en bonen', 'Stevige kabeljauw met chorizo en witte bonen, een Spaanse combinatie.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 25, 40, 'Gemiddeld', 4, '[{"name": "kabeljauwfilet", "quantity": "600", "unit": "g"}, {"name": "chorizo", "quantity": "150", "unit": "g"}, {"name": "witte bonen", "quantity": "400", "unit": "g"}, {"name": "ui", "quantity": "1", "unit": "stuk"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}, {"name": "witte wijn", "quantity": "100", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Bak de chorizo uit in een pan."}, {"step": 2, "instruction": "Voeg ui en knoflook toe."}, {"step": 3, "instruction": "Voeg bonen en wijn toe en laat pruttelen."}, {"step": 4, "instruction": "Leg de kabeljauw erop en laat 10 minuten garen."}, {"step": 5, "instruction": "Serveer met brood."}]'::jsonb, ARRAY['Vis', 'Spaans', 'Comfort Food'], 'Vis'),
(gen_random_uuid(), 'Tonijn Tataki', 'Seared tonijn met sesam en sojasaus, een Japanse delicatesse.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 5, 20, 'Makkelijk', 4, '[{"name": "tonijnsteak", "quantity": "600", "unit": "g"}, {"name": "sesamzaadjes", "quantity": "3", "unit": "el"}, {"name": "sojasaus", "quantity": "3", "unit": "el"}, {"name": "gember", "quantity": "2", "unit": "cm"}, {"name": "lente-ui", "quantity": "3", "unit": "stuk"}, {"name": "sesamolie", "quantity": "1", "unit": "el"}]'::jsonb, '[{"step": 1, "instruction": "Bestrooi de tonijn met sesamzaadjes."}, {"step": 2, "instruction": "Bak de tonijn 30 seconden per kant in een zeer hete pan."}, {"step": 3, "instruction": "De binnenkant moet rauw blijven."}, {"step": 4, "instruction": "Snijd in plakken en serveer met sojasaus, gember en lente-ui."}, {"step": 5, "instruction": "Druppel sesamolie erover."}]'::jsonb, ARRAY['Vis', 'Aziatisch', 'Feest'], 'Vis'),
(gen_random_uuid(), 'Zalm met groene asperges', 'Gebakken zalm met groene asperges en citroen, lente op een bord.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 20, 30, 'Makkelijk', 4, '[{"name": "zalmfilet", "quantity": "600", "unit": "g"}, {"name": "groene asperges", "quantity": "500", "unit": "g"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "boter", "quantity": "50", "unit": "g"}, {"name": "knoflook", "quantity": "2", "unit": "teentjes"}, {"name": "peper", "quantity": "1", "unit": "tl"}]'::jsonb, '[{"step": 1, "instruction": "Kook de asperges 5 minuten."}, {"step": 2, "instruction": "Bak de zalm 4-5 minuten per kant in boter."}, {"step": 3, "instruction": "Bak de asperges mee met knoflook."}, {"step": 4, "instruction": "Pers citroen over alles."}, {"step": 5, "instruction": "Serveer direct."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Kabeljauw met spinazie', 'Gebakken kabeljauw op een bedje van spinazie, licht en gezond.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 20, 30, 'Makkelijk', 4, '[{"name": "kabeljauwfilet", "quantity": "600", "unit": "g"}, {"name": "spinazie", "quantity": "500", "unit": "g"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}, {"name": "room", "quantity": "100", "unit": "ml"}, {"name": "nootmuskaat", "quantity": "1", "unit": "snufje"}, {"name": "boter", "quantity": "30", "unit": "g"}]'::jsonb, '[{"step": 1, "instruction": "Bak de knoflook zachtjes in boter."}, {"step": 2, "instruction": "Voeg spinazie toe en laat slinken."}, {"step": 3, "instruction": "Voeg room en nootmuskaat toe."}, {"step": 4, "instruction": "Bak de kabeljauw 4-5 minuten per kant."}, {"step": 5, "instruction": "Serveer de kabeljauw op de spinazie."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Garnalen met rijst', 'Grote garnalen met rijst en groenten, een complete maaltijd.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 25, 40, 'Gemiddeld', 4, '[{"name": "grote garnalen", "quantity": "600", "unit": "g"}, {"name": "rijst", "quantity": "300", "unit": "g"}, {"name": "ui", "quantity": "1", "unit": "stuk"}, {"name": "paprika", "quantity": "2", "unit": "stuk"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}, {"name": "witte wijn", "quantity": "100", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Kook de rijst volgens de verpakking."}, {"step": 2, "instruction": "Bak de garnalen rondom aan."}, {"step": 3, "instruction": "Voeg ui, paprika en knoflook toe."}, {"step": 4, "instruction": "Giet wijn erbij en laat pruttelen."}, {"step": 5, "instruction": "Serveer de garnalen met de rijst."}]'::jsonb, ARRAY['Vis', 'Comfort Food'], 'Vis'),
(gen_random_uuid(), 'Zalm met zoete aardappel', 'Gebakken zalm met zoete aardappel en broccoli, een gezonde combinatie.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 25, 40, 'Makkelijk', 4, '[{"name": "zalmfilet", "quantity": "600", "unit": "g"}, {"name": "zoete aardappel", "quantity": "600", "unit": "g"}, {"name": "broccoli", "quantity": "400", "unit": "g"}, {"name": "olijfolie", "quantity": "3", "unit": "el"}, {"name": "citroen", "quantity": "1", "unit": "stuk"}, {"name": "peper", "quantity": "1", "unit": "tl"}]'::jsonb, '[{"step": 1, "instruction": "Snijd de zoete aardappel in blokjes en bak 20 minuten in de oven."}, {"step": 2, "instruction": "Kook de broccoli 5 minuten."}, {"step": 3, "instruction": "Bak de zalm 4-5 minuten per kant."}, {"step": 4, "instruction": "Serveer alles samen."}, {"step": 5, "instruction": "Afwerken met citroen."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Kabeljauw met citroenboter', 'Gebakken kabeljauw met een romige citroenboter saus, verfijnd en smaakvol.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 10, 20, 30, 'Makkelijk', 4, '[{"name": "kabeljauwfilet", "quantity": "600", "unit": "g"}, {"name": "boter", "quantity": "100", "unit": "g"}, {"name": "citroen", "quantity": "2", "unit": "stuk"}, {"name": "kappertjes", "quantity": "2", "unit": "el"}, {"name": "peterselie", "quantity": "1", "unit": "bos"}, {"name": "witte wijn", "quantity": "50", "unit": "ml"}]'::jsonb, '[{"step": 1, "instruction": "Bak de kabeljauw 4-5 minuten per kant."}, {"step": 2, "instruction": "Haal de vis uit de pan en houd warm."}, {"step": 3, "instruction": "Voeg wijn, citroensap en kappertjes toe aan de pan."}, {"step": 4, "instruction": "Voeg boter en peterselie toe."}, {"step": 5, "instruction": "Serveer de saus over de kabeljauw."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis'),
(gen_random_uuid(), 'Garnalen Paella', 'Klassieke Spaanse paella met garnalen, een feestelijk gerecht.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 20, 35, 55, 'Moeilijk', 6, '[{"name": "garnalen", "quantity": "600", "unit": "g"}, {"name": "rijst", "quantity": "400", "unit": "g"}, {"name": "saffraan", "quantity": "1", "unit": "snufje"}, {"name": "paprika", "quantity": "2", "unit": "stuk"}, {"name": "ui", "quantity": "1", "unit": "stuk"}, {"name": "knoflook", "quantity": "3", "unit": "teentjes"}]'::jsonb, '[{"step": 1, "instruction": "Bak ui, paprika en knoflook aan."}, {"step": 2, "instruction": "Voeg rijst en saffraan toe."}, {"step": 3, "instruction": "Giet bouillon erbij en laat 20 minuten koken."}, {"step": 4, "instruction": "Voeg de garnalen toe en laat 5 minuten garen."}, {"step": 5, "instruction": "Serveer direct uit de pan."}]'::jsonb, ARRAY['Vis', 'Spaans', 'Feest'], 'Vis'),
(gen_random_uuid(), 'Zalm met quinoa', 'Gebakken zalm met quinoa en groenten, een gezonde en complete maaltijd.', 'Dietmar Lattré', 'https://images.unsplash.com/photo-1544943910-4c1dc44aab44?auto=format&fit=crop&w=800&q=80', 15, 25, 40, 'Makkelijk', 4, '[{"name": "zalmfilet", "quantity": "600", "unit": "g"}, {"name": "quinoa", "quantity": "200", "unit": "g"}, {"name": "komkommer", "quantity": "1", "unit": "stuk"}, {"name": "tomaat", "quantity": "2", "unit": "stuk"}, {"name": "feta", "quantity": "100", "unit": "g"}, {"name": "olijfolie", "quantity": "3", "unit": "el"}]'::jsonb, '[{"step": 1, "instruction": "Kook de quinoa volgens de verpakking."}, {"step": 2, "instruction": "Bak de zalm 4-5 minuten per kant."}, {"step": 3, "instruction": "Snijd komkommer en tomaat in blokjes."}, {"step": 4, "instruction": "Meng quinoa met groenten en feta."}, {"step": 5, "instruction": "Serveer de zalm met de quinoa salade."}]'::jsonb, ARRAY['Vis', 'High Protein'], 'Vis')
insert into public.recipe_categories (recipe_id, category)
